"use strict";function askName(){var t=prompt("Enter your name");alert("Hello, ".concat(t,"!"))}function askBirthday(){var t=prompt("У якому ти році народився/лась?");alert("Тобі приблизно "+(2025-t)+" років.")}function askPerimeter(){var t=prompt("Введи довжину сторони квадрата (у см):");alert("Периметр квадрата: "+4*t+" см")}function askCircleArea(){var t=Number(prompt("Введіть радіус кола в сантиметрах:")),r=Math.PI*Math.pow(t,2);alert("Площа кола з радіусом ".concat(t," см = ").concat(r.toFixed(2)," см²"))}function convertUsdToEur(){var t=Number(prompt("Введіть суму в доларах (USD):")),r=.85*t;alert("".concat(t," USD = ").concat(r.toFixed(2)," EUR"))}